@use "abstracts/variables" as *;
@use "../abstracts/mixins" as *;

@keyframes moveInLeft {
  0% {
    opacity: 0;
    transform: translateX(-30.25rem);
  }

  80% {
    transform: translateX(0.0625rem);
  }

  100% {
    opacity: 1;
    transform: translate(0);
  }
}

@keyframes moveInRight {
  0% {
    opacity: 0;
    transform: translateX(30.25rem);
  }

  80% {
    transform: translateX(-0.0625rem);
  }

  100% {
    opacity: 1;
    transform: translate(0);
  }
}

