// COLORS
$galactic-blue: rgb(117, 92, 222);
$summer-yellow: rgb(246, 165, 96);
$pink: rgb(243, 158, 158);
$light-red: rgb(235, 117, 101);
$cyan: rgb(97, 196, 183);
$dark-purple: rgb(85, 32, 73);
$black: rgb(3, 3, 3);
$medium-brown: rgb(122, 116, 110);
$light-cream: rgb(255, 247, 240);
$btn-text: #fff7f0;
$color-white: #ffffff;

// TYPOGRAPHY
// global grid-text
$font-weight-lg: 700;
$font-size-big: 1.5rem;
$line-height-big: 1.875rem;

// heading S
$font-size-S: 2rem;
$line-height-S: 125%; //2.5rem

// heading M
$font-size-M: 2.5rem;
$line-height-M: 125%; //3.125rem

// Small Screens
// btn-text
$font-size-small: 0.875rem;
$line-height-primary: 200%;

// body s
$font-weight-sm: 500;
$font-size-medium: 1rem;
$line-height-small: 162%;

// Large Screens
// btn-text
$line-height-medium: 175%;

// body m
$font-size-body: 1.125rem;
$line-height-body: 156%;

// SPACING
$sides-small: 1rem;
$sides-medium: 2.5rem;
$sides-large: 10.3125rem;

sources.scss\\abstracts\\_mixins.scss:
// MEDIA QUERY MANAGER

@mixin responsive($breakpoint) {
  @if $breakpoint == tablet {
    @media only screen and (min-width: 37.5rem) {
      @content;
    } //600px
  }
  @if $breakpoint == desktop {
    @media only screen and (min-width: 61.125rem) {
      @content;
    } //978px
  }
}

sources.scss\\components\\_buttons.scss:
@use "../abstracts/variables" as *;
@use "../abstracts/mixins" as *;

.btn {
  font-weight: $font-weight-lg;
  font-size: $font-size-small;
  line-height: $line-height-primary;
  text-decoration: none;
  color: $btn-text;
  padding: 0.5em 1.75em;
  background: $black;
  border-radius: 1.75rem;
  border: none;
  display: inline-block;
  position: relative;
  overflow: hidden;
  transition: color 0.5s;
  z-index: 1;

  @include responsive(tablet) {
    font-size: $font-size-medium;
    line-height: $line-height-medium;
    padding: 0.875em 2.8125em;
  }

  &:before {
    content: "";
    z-index: -1;
    background: $galactic-blue;
    height: 12.5rem;
    width: 15.625rem;
    border-radius: 50%;
    position: absolute;
    top: 100%;
    left: 100%;
    transition: all 0.7s;

    @include responsive(tablet) {
      height: 14.375rem;
      width: 18.125rem;
    }
  }

  &:hover:before {
    top: -1.875rem;
    left: -1.875rem;
  }

  &:active:before {
    background: $galactic-blue;
    transition: background 0s;
  }
}

.btn-secondary {
  background: $pink;

  &:before {
    background: $summer-yellow;
  }

  &:active:before {
    background: $summer-yellow;
  }
}

.btn-bio {
  @include responsive(tablet) {
    align-self: flex-start;
  }
}

sources.scss\\components\\_slider.scss:
@use "../abstracts/variables" as *;
@use "../abstracts/mixins" as *;

.carousel {
  display: flex;
  flex-direction: column;
  gap: 1rem;
  margin-bottom: 6.25rem;
  overflow-x: hidden;

  @include responsive(tablet) {
    gap: 3.5rem;
    margin-bottom: 7.5rem;
  }

  @include responsive(desktop) {
    margin-bottom: 5.5rem;
  }

  .slider {
    display: flex;
    justify-content: center;
    gap: 0.9375rem;
    transition: all 0.9s ease-in-out;

    .slider-img {
      border-radius: 0.625rem;
    }

    @include responsive(tablet) {
      gap: 1.875rem;
    }
  }

  .slider-btn-container {
    display: flex;
    justify-content: center;
    gap: 1rem;

    .slider-btn {
      background: $black;
      border: none;
      cursor: pointer;
      border-radius: 100%;
      padding: 1.5rem;
      transition: all 0.7s;
      z-index: 1000;

      &:hover {
        background: $galactic-blue;
      }
    }
  }
}

