sources.scss\\layout\\_grid.scss:
@use "../abstracts/variables" as *;
@use "../abstracts/mixins" as *;

.grid-wrapper {
  margin-bottom: 6.25rem;
  padding: 0 $sides-small;

  @include responsive(tablet) {
    margin-bottom: 7.5rem;
    padding: 0 $sides-medium;
  }

  @include responsive(desktop) {
    margin-bottom: 8.5rem;
    padding: 0 $sides-large;
  }

  .grid-container {
    display: grid;
    place-content: center;
    gap: 1.5rem;
    grid-template-columns: 1fr 1fr;
    // grid-template-rows: repeat(4, 1fr);
    grid-template-areas:
      "graphic graphic"
      "ui apps"
      "illust illust"
      "photo photo"
      "motion motion";

    @include responsive(tablet) {
      grid-template-columns: repeat(4, 1fr);
      grid-template-rows: repeat(3, 1fr);
      grid-template-areas:
        "graphic graphic ui apps"
        "graphic graphic illust illust"
        "photo photo motion motion";
    }

    @include responsive(desktop) {
      grid-template-columns: repeat(6, 1fr);
      grid-template-rows: repeat(2, 1fr);
      grid-template-areas:
        "graphic graphic ui apps photo photo"
        "graphic graphic illust illust motion motion";
    }

    .item {
      border-radius: 0.5rem;
      padding: 1.5rem;
      display: flex;
      flex-direction: column-reverse;
      justify-content: space-between;
      align-items: center;

      .pattern {
        align-self: flex-end;
        margin-bottom: 1rem;
      }
    }

    .graphic {
      background-color: $galactic-blue;
      grid-area: graphic;
    }

    .ui-ux {
      background-color: $summer-yellow;
      grid-area: ui;
    }

    .apps {
      background-color: $pink;
      grid-area: apps;
    }

    .illust {
      background-color: $light-red;
      grid-area: illust;
    }

    .photo {
      background-color: $cyan;
      grid-area: photo;
    }

    .motion {
      background-color: $dark-purple;
      grid-area: motion;
    }
  }
}

sources.scss\\pages\\_home.scss:
@use "../abstracts/variables" as *;
@use "../abstracts/mixins" as *;

.intro {
  text-align: center;
  margin-bottom: 2rem;
  padding: $sides-small;

  @include responsive(tablet) {
    margin-bottom: 4rem;
  }

  @include responsive(desktop) {
    margin-bottom: 5rem;
  }
}

.author {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  gap: 2.5rem;
  padding: 0 $sides-small;
  margin-bottom: 6.25rem;

  @include responsive(tablet) {
    flex-direction: row;
    gap: 4.3125rem;
    justify-content: center;
    padding-right: 1rem;
    margin-bottom: 7.5rem;
  }

  @include responsive(desktop) {
    gap: 7.8125rem;
    padding: 0 $sides-large;
    margin-bottom: 5.5rem;
  }

  .bio {
    display: flex;
    align-items: center;
    flex-direction: column;
    gap: 1.5rem;

    @include responsive(tablet) {
      text-align: left;
    }

    @include responsive(desktop) {
      gap: 2rem;
    }
  }

  .author-img-container {
    max-width: 27.8125rem;
  }
}

.contact-container {
  background: $black;
  margin: 0 $sides-small 2.5rem;
  text-align: center;
  border-radius: 0.625rem;

  @include responsive(tablet) {
    margin: 0 $sides-medium 3.5rem;
  }

  @include responsive(desktop) {
    margin: 0 $sides-large 3.5rem;
    padding-inline: 1.25rem;
  }

  .contact {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 1.5rem;
    padding: 3rem 1.5rem;

    @include responsive(tablet) {
      gap: 1.625rem;
      padding-block: 2.5rem;
      padding-top: 4.6875rem;
      padding-bottom: 4.6875rem;
    }

    @include responsive(desktop) {
      flex-direction: row;
      justify-content: space-between;
      gap: 3.25rem;
      padding-block: 2.5rem;
      padding-top: 5rem;
      padding-bottom: 5rem;
    }

    .contact-text {
      display: flex;
      align-items: center;
      flex-direction: column;
      gap: 1.5rem;

      @include responsive(tablet) {
        gap: 1.625rem;
      }

      @include responsive(desktop) {
        gap: 1.5625rem;
        text-align: left;
        align-items: flex-start;
        width: 50%;
      }
    }
  }
}
